import axios, { HttpStatusCode } from 'axios';
import React, { useState } from 'react'
import Swal from 'sweetalert2';
import Loader from '../Loader'
const NotesItem = (props) => {
    const note = props.note;
    const [isLoading, setIsLoading] = useState(false);
    const deleteNote=(id,categoryName)=>{
        setIsLoading((prevState) => !prevState)
        const config = {
            headers: {
                'Authorization': localStorage.getItem('tokenForValidation')
            }
        };
        let url = `http://localhost:9092/user/deleteNote?categoryName=${categoryName}`
        let body={'id':id}
        axios.delete(url,body,config).then(res => {
            if (res.status === HttpStatusCode.Ok) {
                Swal.fire({ title: "Success!", text: `Note deleted with id ${id}`, icon: 'success' })
            }
        }).catch(err => {
            if (err.response) {
                Swal.fire(`Failed`, `${err.response.data}`, `error`)
            } else {
                Swal.fire({ title: "Error occured Try again!!!", icon: 'error' })
            }
        }).finally(()=>{setIsLoading(() => { return false })})
    }
    return (
        <>
            <div className="card border-info mb-3">
                <div className="card-header bg-dark text-white d-flex justify-content-between">
                    <div>
                        {`#${note.id}`}
                        <span className='mx-3 mt-1' onClick={()=>{deleteNote(note.id,props.categoryName)}}><i className="fa-solid fa-trash text-white" style={{cursor : 'pointer'}}></i></span>
                        <span><i className="fa-solid fa-pencil text-white" style={{cursor : 'pointer'}}></i></span>
                    </div>
                    <div>{`Created on : ${note.createdDate}`}</div>
                </div>
                {isLoading ? (<div className="mt-4"> <Loader /></div>) : (
                    <div className="card-body">
                    <h5 className="card-title">{`${note.title}`}</h5>
                    <p className="card-text">{note.content}</p>
                </div>
                )}
                <div className="card-footer bg-transparent border-success">{`Last Modified on : ${note.lastModifiedDate === null ? 'None' : note.lastModifiedDate}`}</div>
            </div>
        </>
    )
}

export default NotesItem
