import React from 'react'

function EditDetails() {

    const [name,setName]=useState('')

    const updateName=(e)=>{
        setName(e.target.value)
    }
    return (
        <>
            <div className="text-center fs-2 mt-1" style={{ fontFamily: 'Ubuntu', fontWeight: 'bolder' }}>Edit Name :-</div>
            <div className="mb-2 mt-1">
                <input type="text" placeholder='enter updated name here and press submit button' value={name} onChange={updateName} className="form-control" />
            </div><hr />
            <div className="text-center fs-2 mt-1" style={{ fontFamily: 'Ubuntu', fontWeight: 'bolder' }}>Edit Name :-</div>
            <div className="mb-2 mt-1">
                <input type="text" placeholder='enter updated name here and press submit button' value={name} onChange={updateName} className="form-control" />
            </div>
        </>
    )
}

export default EditDetails
