import React, { useState } from 'react'
import todoContext from './TodoContext'
import axios, { HttpStatusCode } from 'axios'
import Swal from 'sweetalert2'
const TodoState = (props) => {
    const initialTodos = []
    const [todos, setTodos] = useState(initialTodos)
    const getTodos=()=>{
        let config = {
            headers: {
                'Authorization': localStorage.getItem('tokenForValidation')
            }
        }
        let url = 'http://localhost:9092/user/getTodos'
        axios.get(url, config).then(response => {
            if (response.status === HttpStatusCode.Ok) {
                setTodos(response.data)
            }
        }).catch(error => {
            if (error.response) {
                Swal.fire({ title: "Error !", icon: 'error', text: `${error.response.data}` })
            } else {
                Swal.fire("Something went wrong", "Please try again later", 'error');
            }
        })
    }
    return (
        <todoContext.Provider value={{todos,setTodos,getTodos}}>
            {props.children}
        </todoContext.Provider>
    )
}

export default TodoState
