  return (
    <>
      <div className="row">
        <div className="col">
          <button className="btn btn-md m-2" style={{ background: 'rgba(139,129,218,255)', color: 'white' }} onClick={addTodo}><i className="fa-regular fa-square-plus"></i> Add TODO</button>
          <button className="btn btn-md m-2 " style={{ background: 'rgba(139,129,218,255)', color: 'white' }} onClick={addNote}><i className="fa-regular fa-square-plus"></i> Add Note</button>
        </div>
        {/* <img src="/welcome.jpg" style={{width :'30em' , height : '30em' }} alt=""/> */}
      </div>
      <div className="row">
        <div className="col">
          <h2>We ensures your safety</h2>
          <h3>Security and Privacy</h3>
          <p  style={{ fontFamily: "poppins", fontSize: "2em" }}>Introducing NotesApp, the ultimate digital companion for capturing, organizing, and remembering your thoughts. Whether you're brainstorming, making to-do lists, or simply jotting down ideas, NotesApp offers a seamless and versatile note-taking experience that adapts to your needs. <br />
          With multi-device synchronization, your notes are available wherever you are, be it on your smartphone during your commute, on your laptop at work, or on your tablet at home. The app's intuitive interface makes note-taking a breeze, and you can effortlessly categorize and tag your notes for easy retrieval.
          </p>
        </div>
        <div className="col img"></div>
      </div>
    </>
  )
}

export default HomeContent
