import axios, { HttpStatusCode } from 'axios'
import React from 'react'
import Swal from 'sweetalert2'

const TodoItem = (props) => {
    const todo = props.todo
    const setTodos=props.setTodos
    const todos=props.todos
    console.log(todo)
    const updateTodo=(id)=>{
        Swal.fire({
            title: 'Enter TODO to update',
            input: 'text',
            inputAttributes: {
                autocapitalize: 'off'
            },
            showCancelButton: true,
            confirmButtonText: 'Update',
            showLoaderOnConfirm: true,
            preConfirm: (description) => {
                const config = {
                    headers: {
                        'Authorization': localStorage.getItem('tokenForValidation')
                    }
                };
                let url = `http://localhost:9092/user/updateTodoDescription`
                let data = { 'description': description, 'id': id }
                return axios.put(url, data, config).then(res => {
                    if (res.status === HttpStatusCode.Ok) {
                        Swal.fire({ title: "Success!", text: `TODO changed with ${description}`, icon: 'success' })
                        let newTodos=todos.map(todo=>{
                            if(todo.id===id){
                                todo.description=description
                            }
                            return todo;
                        })
                        setTodos(newTodos)
                    }
                }).catch(err => {
                    if (err.response) {
                        Swal.showValidationMessage(`Failed : ${err.response.data}`)
                    } else {
                        Swal.showValidationMessage("Something went wrong!")
                    }
                })
            },
            allowOutsideClick: () => !Swal.isLoading()
        })
    }
    return (
        <>
            <div className="container mt-2">
                <div className="container">
                    <div className="bg-dark text-white m-2 p-2" style={{ borderRadius: '8px' }}>
                        <div className='mx-1'>
                            <i onClick={()=>{updateTodo(todo.id)}} style={{cursor : 'pointer'}} className="mx-2 fa-solid fa-pen"></i>
                            <i style={{cursor : 'pointer'}} className="mx-2 fa-solid fa-trash-can"></i>
                            <i style={{cursor : 'pointer'}} className="mx-2 fa-sharp fa-solid fa-check"></i>
                        </div><hr className='m-1' />
                        <span className='mx-2' style={{textDecoration : (todo.isCompleted ==='false') ? 'none' : 'line-through red'}}>
                            {todo.description}
                        </span>
                    </div>
                </div>
            </div>
        </>
    )
}

export default TodoItem
