import React, { useState } from 'react'

const AddCatAndTodoForm = () => {

  const [category, setCategory] = useState("")

  const updateCategory = (e) => {
    setCategory(e.target.value)
  }

  const [todo,setTodo]=useState("")
  const updateTodo = (e) => {
    setTodo(e.target.value)
  }

  return (
    <>
      <div className="text-center fs-2 mt-1" style={{ fontFamily: 'Ubuntu', fontWeight: 'bolder' }}>Add Category :-</div>
      <div className="mb-2 mt-1">
        <label htmlFor="categoryName" className="form-label">Category name <span className='text-danger'>*</span></label>
        <input type="text" value={category} onChange={updateCategory} className="form-control" id="categoryName" aria-describedby="emailHelp" required={true} />
        <div className="text-center mt-3"><button type="button" className="btn btn-primary ">Add Note</button></div>
      </div><hr />
      <div className="text-center fs-2 mt-1" style={{ fontFamily: 'Ubuntu', fontWeight: 'bolder' }}>Add a TODO :-</div>
      <div className="mb-2 mt-1">
        <label htmlFor="description" className="form-label">TODO <span className='text-danger'>*</span></label>
        <input type="text" value={todo} onChange={updateTodo} className="form-control" id="description" aria-describedby="emailHelp" required={true} />
        <div className="text-center mt-3"><button type="button" className="btn btn-primary ">Add Note</button></div>
      </div>
    </>
  )
}

export default AddCatAndTodoForm
